-- Module 2: Understanding Jito (5 sections)
INSERT INTO module_content (module_id, section_order, section_type, title, content) VALUES
(2, 1, 'text', 'What is Jito?', '{"text": "Jito is a MEV infrastructure protocol for Solana that enables validators to capture MEV rewards while maintaining network decentralization. It provides a marketplace where searchers can submit transaction bundles to validators.", "keyPoints": ["Jito enables atomic transaction bundles on Solana", "Validators earn additional revenue from MEV", "Searchers get guaranteed execution or revert", "Reduces network spam from failed MEV attempts"]}'),
(2, 2, 'text', 'How Jito Works', '{"text": "Jito operates through a modified Solana validator client that accepts bundles from searchers. These bundles are atomic - either all transactions execute or none do.", "keyPoints": ["Searchers submit bundles via Jito Block Engine", "Validators run Jito-Solana client", "Bundles compete via tips to validators", "Failed bundles don''t consume blockspace"]}'),
(2, 3, 'text', 'Jito vs Regular Validators', '{"comparison": [{"aspect": "MEV Capture", "regular": "Limited", "jito": "Full MEV capture", "benefit": "Higher validator revenue"}, {"aspect": "Bundle Support", "regular": "No", "jito": "Yes", "benefit": "Atomic execution guarantees"}, {"aspect": "Searcher Access", "regular": "Public mempool only", "jito": "Direct bundle submission", "benefit": "Better execution rates"}], "keyPoints": ["Jito validators earn 5-15% more revenue", "Bundle success rates are 3-5x higher", "Reduced network congestion from failed attempts"]}'),
(2, 4, 'text', 'Benefits for Searchers', '{"text": "Searchers benefit from guaranteed atomic execution, higher success rates, and protection from front-running by other bots.", "keyPoints": ["Atomic bundles prevent partial execution", "Higher success rates (60-80% vs 20-40%)", "Protection from sandwich attacks", "Lower overall costs due to fewer failed attempts"]}'),
(2, 5, 'text', 'Jito Economics', '{"text": "Jito creates a sustainable MEV economy where validators, searchers, and the network all benefit. Tips flow from searchers to validators based on bundle value.", "keyPoints": ["Searchers pay tips (0.1-5% of MEV)", "Validators earn tips + block rewards", "Network benefits from reduced spam", "Transparent and competitive marketplace"]}');

