-- Module 4: Liquidation Hunting (6 sections)
INSERT INTO module_content (module_id, section_order, section_type, title, content) VALUES
(4, 1, 'text', 'Liquidation Basics', '{"text": "Liquidations occur when a borrower''s collateral value falls below the required threshold on lending protocols. Liquidators can repay the debt and claim collateral at a discount.", "keyPoints": ["Lending protocols require overcollateralization", "Health factor < 1.0 triggers liquidation", "Liquidators earn 5-15% bonus", "Speed is critical - first liquidator wins"]}'),
(4, 2, 'text', 'Health Factor Calculation', '{"text": "Health factor determines if a position can be liquidated. It''s calculated as: (Collateral Value × Liquidation Threshold) / Borrowed Value", "keyPoints": ["Health Factor > 1.0 = Safe", "Health Factor = 1.0 = At risk", "Health Factor < 1.0 = Can be liquidated", "Monitor positions approaching 1.0"]}'),
(4, 3, 'text', 'Finding Opportunities', '{"text": "Liquidation opportunities are found by monitoring lending protocols for positions with declining health factors.", "keyPoints": ["Query lending protocol APIs", "Monitor price feeds for collateral assets", "Track positions near liquidation threshold", "Set up alerts for health factor < 1.05"]}'),
(4, 4, 'text', 'Liquidation Mechanics', '{"text": "Executing a liquidation involves repaying part or all of the debt and receiving collateral plus a bonus.", "keyPoints": ["Repay debt in borrowed asset", "Receive collateral + liquidation bonus (5-15%)", "Partial liquidations allowed on some protocols", "Must have capital to repay debt"]}'),
(4, 5, 'text', 'Profitability Analysis', '{"text": "Calculate liquidation profit by considering bonus, gas costs, and price impact of selling collateral.", "keyPoints": ["Profit = (Collateral × Bonus %) - Debt - Gas - Slippage", "Larger liquidations more profitable", "Consider time to sell collateral", "Account for market volatility"]}'),
(4, 6, 'text', 'Risk Management', '{"text": "Liquidation hunting carries risks including price volatility, competition, and smart contract risks.", "keyPoints": ["Collateral price may drop during execution", "Competition from other liquidators", "Gas wars can eliminate profits", "Smart contract bugs or exploits"]}');

