-- Module 6: Bundle Construction (7 sections)
INSERT INTO module_content (module_id, section_order, section_type, title, content) VALUES
(6, 1, 'text', 'Bundle Fundamentals', '{"text": "Bundles are groups of transactions that execute atomically - either all succeed or all fail. This is critical for complex MEV strategies.", "keyPoints": ["Atomic execution prevents partial failures", "Transactions execute in specified order", "Failed bundles don''t consume gas", "Enables complex multi-step strategies"]}'),
(6, 2, 'text', 'Transaction Ordering', '{"text": "The order of transactions in a bundle is critical for success. Wrong ordering can cause the entire bundle to fail.", "keyPoints": ["Dependencies must execute first", "Approvals before transfers", "Swaps before arbitrage completion", "Consider state changes between transactions"]}'),
(6, 3, 'text', 'Gas Estimation', '{"text": "Accurate gas estimation ensures your bundle has enough compute units to execute fully.", "keyPoints": ["Estimate each transaction separately", "Add 10-20% buffer for safety", "Monitor compute unit limits (1.4M per transaction)", "Higher gas = higher priority"]}'),
(6, 4, 'text', 'Bundle Tips', '{"text": "Tips incentivize validators to include your bundle. Higher tips increase inclusion probability.", "keyPoints": ["Tip based on expected profit (1-5%)", "Competitive opportunities need higher tips", "Tips go to validators, not burned", "Balance tip vs profit margin"]}'),
(6, 5, 'text', 'Error Handling', '{"text": "Bundles can fail for many reasons. Understanding failure modes helps you build robust bundles.", "keyPoints": ["Slippage tolerance too tight", "Insufficient gas", "State changes between simulation and execution", "Account data race conditions"]}'),
(6, 6, 'text', 'Simulation Testing', '{"text": "Always simulate bundles before submission to catch errors and estimate outcomes.", "keyPoints": ["Use Jito simulation API", "Test with current blockchain state", "Verify all transactions succeed", "Check final profit after costs"]}'),
(6, 7, 'text', 'Advanced Techniques', '{"text": "Professional bundle builders use advanced techniques to maximize success and profitability.", "keyPoints": ["Multi-bundle strategies for redundancy", "Dynamic tip adjustment based on competition", "State prefetching to reduce latency", "Custom priority fee strategies"]}');

INSERT INTO quizzes (module_id, title, passing_score, questions) VALUES
