  {"id": 8, "question": "What do bundle tips incentivize?", "options": ["Faster blocks", "Validator inclusion", "Lower fees", "More MEV"], "correct": 1, "explanation": "Tips incentivize validators to include your bundle in their blocks."},
  {"id": 9, "question": "When should you use multi-bundle strategies?", "options": ["Never", "For redundancy and higher success rates", "Only for small profits", "Always"], "correct": 1, "explanation": "Multi-bundle strategies provide redundancy and increase overall success rates."},
  {"id": 10, "question": "What causes most bundle failures?", "options": ["Low tips", "Slippage and state changes", "Validator issues", "Network congestion"], "correct": 1, "explanation": "Most bundle failures are caused by slippage tolerance and state changes between simulation and execution."}
]');

-- Module 7: Risk Management (6 sections)
INSERT INTO module_content (module_id, section_order, section_type, title, content) VALUES
(7, 1, 'text', 'MEV Risk Categories', '{"text": "MEV extraction involves multiple risk categories that can impact profitability and capital.", "keyPoints": ["Execution risk: Transaction failures", "Market risk: Price volatility", "Competition risk: Other bots", "Technical risk: Smart contract bugs", "Regulatory risk: Legal uncertainty"]}'),
(7, 2, 'text', 'Position Sizing', '{"text": "Never risk more capital than you can afford to lose. Proper position sizing protects your capital.", "keyPoints": ["Risk 1-5% of capital per opportunity", "Larger positions for higher confidence trades", "Scale position with win rate", "Keep reserve capital for opportunities"]}'),
(7, 3, 'text', 'Stop Loss Strategies', '{"text": "Set clear exit criteria to limit losses on failed strategies.", "keyPoints": ["Maximum loss per trade", "Daily loss limits", "Strategy performance thresholds", "Automatic shutdown on anomalies"]}'),
(7, 4, 'text', 'Diversification', '{"text": "Don''t rely on a single MEV strategy. Diversify across multiple opportunity types.", "keyPoints": ["Mix arbitrage, liquidations, and other strategies", "Trade multiple token pairs", "Use multiple DEXs", "Spread across time periods"]}'),
(7, 5, 'text', 'Monitoring and Alerts', '{"text": "Continuous monitoring helps you catch issues before they become costly.", "keyPoints": ["Track success rates by strategy", "Monitor gas costs vs profits", "Set alerts for anomalies", "Review performance daily"]}'),
(7, 6, 'text', 'Risk-Adjusted Returns', '{"text": "Evaluate strategies based on risk-adjusted returns, not just absolute profits.", "keyPoints": ["Sharpe Ratio: Return per unit of risk", "Maximum drawdown: Largest loss period", "Win rate: Percentage of profitable trades", "Profit factor: Gross profit / Gross loss"]}');

INSERT INTO quizzes (module_id, title, passing_score, questions) VALUES
